mapper.select:
select
`id`,`os_ip`,`os_id`,`info`,`details`,`result`,`entry_time`,`level`,`type`,`be_type`,`log_type`,`notes`,`create_by`,`create_time`,`update_by`,`update_time`
from cli_log

	 and id = #{id} 
	 and os_ip = #{osIp} 
	 and os_id = #{osId} 
	 and info = #{info} 
	 and details LIKE '%'||#{details}||'%' 
	 and result = #{result} 
	 and entry_time = #{entryTime} 
	 and level = #{level} 
	 and type = #{type} 
	 and be_type = #{beType} 
	 and log_type = #{logType} 
	 and notes = #{notes} 
	 and create_by = #{createBy} 
	 and create_time = #{createTime} 
	 and update_by = #{updateBy} 
	 and update_time = #{updateTime} 
	 and id > #{logQueryId} 
	and entry_time >= #{startTime}
	and entry_time<= #{endTime}



		order by ${sort} ${order}


		order by id desc



	limit #{offset}, #{limit}


	limit #{limit}

select count(*) from cli_log

	 and id = #{id} 
	 and os_ip = #{osIp} 
	 and os_id = #{osId} 
	 and info = #{info} 
	 and details LIKE '%'||#{details}||'%'   
	 and result = #{result} 
	 and entry_time = #{entryTime} 
	 and level = #{level} 
	 and type = #{type} 
	 and be_type = #{beType} 
	 and log_type = #{logType} 
	 and notes = #{notes} 
	 and create_by = #{createBy} 
	 and create_time = #{createTime} 
	 and update_by = #{updateBy} 
	 and update_time = #{updateTime} 
	and entry_time >= #{startTime}
	and entry_time<= #{endTime}

select
t.`id`,t.`os_ip`,t.`os_id`,t.`info`,t.`details`,t.`result`,t.`entry_time`,t.`level`,t.`type`,t.`be_type`,t.`log_type`,t.`notes`,t.`create_by`,t.`create_time`,t.`update_by`,t.`update_time`
,oi.dept_name,oi.person_liable_name,oi.os_name,t.node_id,t.log_id,t.program
from cli_log t
LEFT JOIN ahy_os_info oi on oi.os_ip=t.os_ip

	 and t.id = #{id} 
	 and t.os_ip = #{osIp} 
	 and t.os_id = #{osId} 
	 and t.info = #{info} 
	 and t.details LIKE CONCAT('%',#{details},'%') 
	 and t.result = #{result} 
	 and t.entry_time = #{entryTime} 
	 and t.level = #{level} 
	 and t.type = #{type} 
	 and t.be_type = #{beType} 
	 and t.log_type = #{logType} 
	 and t.notes = #{notes} 
	 and t.create_by = #{createBy} 
	 and t.create_time = #{createTime} 
	 and t.update_by = #{updateBy} 
	 and t.update_time = #{updateTime} 
	 and oi.dept_id = #{deptId} 




		order by ${sort} ${order}


		order by t.id desc



	limit #{offset}, #{limit}

select count(*) from cli_log t LEFT JOIN ahy_os_info oi on
oi.os_ip=t.os_ip

	 and id = #{id} 
	 and t.os_ip = #{osIp} 
	 and os_id = #{osId} 
	 and info = #{info} 
	 and details LIKE CONCAT('%',#{details},'%')  
	 and result = #{result} 
	 and entry_time = #{entryTime} 
	 and level = #{level} 
	 and type = #{type} 
	 and be_type = #{beType} 
	 and log_type = #{logType} 
	 and notes = #{notes} 
	 and create_by = #{createBy} 
	 and create_time = #{createTime} 
	 and update_by = #{updateBy}

