	 and os_id = #{osId} 
	 and info = #{info} 
	 and details LIKE CONCAT('%',#{details},'%') 
	 and result = #{result} 
	 and entry_time = #{entryTime} 
	 and level = #{level} 
	 and type = #{type} 
	 and be_type = #{beType} 
	 and log_type = #{logType} 
	 and notes = #{notes} 
	 and create_by = #{createBy} 
	 and create_time = #{createTime} 
	 and update_by = #{updateBy} 
	 and update_time = #{updateTime} 
	 and id > #{logQueryId} 




		order by ${sort} ${order}


		order by id desc



	limit #{offset}, #{limit}


